import React from "react";

//Local stuff.
import "../styles/main.scss";

//Bootstrap.
import Form from "react-bootstrap/Form";

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hex: "",
      decimal: "",
      octal: "",
      binary: "",
    };

    this.handleHexChange = this.handleHexChange.bind(this);
    this.handleDecimalChange = this.handleDecimalChange.bind(this);
    this.handleOctalChange = this.handleOctalChange.bind(this);
    this.handleBinaryChange = this.handleBinaryChange.bind(this);
  }

  handleHexChange(event) {
    this.setState({
      hex: event.target.value,
      decimal: parseInt(event.target.value, 16),
    });
  }

  handleDecimalChange(event) {
    this.setState({ decimal: event.target.value });
  }

  handleOctalChange(event) {
    this.setState({ octal: event.target.value });
  }

  handleBinaryChange(event) {
    this.setState({ binary: event.target.value });
  }

  render() {
    return (
      <div className="Home">
        <div className="center-horizontally">
          <div className="column">
            <h1 className="header heavy-font">CompSciCalculator</h1>
            <div className="divider"></div>
          </div>
        </div>

        <div className="center-horizontally">
          <div className="column">
            <div className="converter-container">
              <h3 className="description light-font">
                Enter a number in any box to convert to another base.
              </h3>

              <Form variant="dark">
                <Form.Group controlId="exampleForm.ControlSelect1">
                  <Form.Label>Number Format</Form.Label>
                  <Form.Control as="select">
                    <option>Number</option>
                    <option>Signed 8-bit</option>
                    <option>Signed 16-bit</option>
                    <option>Signed 32-bit</option>
                    <option>Signed 64-bit</option>
                  </Form.Control>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Hex</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows="1"
                    value={this.state.hex}
                    onChange={this.handleHexChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Decimal</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows="1"
                    value={this.state.decimal}
                    onChange={this.handleDecimalChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Octal</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows="1"
                    value={this.state.octal}
                    onChange={this.handleOctalChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Binary</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows="1"
                    value={this.state.binary}
                    onChange={this.handleBinaryChange}
                  />
                </Form.Group>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
