import React from "react";

//Local stuff.
import "../styles/main.scss";

//Bootstrap.
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";

class Home extends React.Component {
  constructor(props) {
    super(props);
    var numFormat = "Number";

    this.state = {
      numFormat: numFormat,
    };
  }

  render() {
    return (
      <div className="Home">
        <div className="center-horizontally">
          <div className="column">
            <h1 className="header heavy-font">CompSciCalculator</h1>
            <div className="divider"></div>
          </div>
        </div>

        <div className="center-horizontally">
          <div className="column">
            <div className="converter-container">
              <h3 className="description light-font">
                Enter a number in any box to convert to another base.
              </h3>

              <p className="num-format">Number Format</p>
              <DropdownButton
                id="dropdown-item-button"
                title={this.state.numFormat}
              >
                <Dropdown.Item as="button">Number</Dropdown.Item>
                <Dropdown.Item as="button">
                  <div
                    onPointerDown={() =>
                      this.setState({ numFormat: "Signed 8-bit" })
                    }
                  >
                    Signed 8-bit
                  </div>
                </Dropdown.Item>
                <Dropdown.Item as="button">Signed 16-bit</Dropdown.Item>
                <Dropdown.Item as="button">Signed 32-bit</Dropdown.Item>
                <Dropdown.Item as="button">Signed 64-bit</Dropdown.Item>
              </DropdownButton>

              <p className="num-format">Hex</p>
              <div className="place-holder"></div>

              <p className="num-format">Decimal</p>
              <div className="place-holder"></div>

              <p className="num-format">Octal</p>
              <div className="place-holder"></div>

              <p className="num-format">Binary</p>
              <div className="place-holder"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
